    public function cuentarray($arreglo) {
        if (is_array($arreglo)) {
            $cuantos = count($arreglo["id"]);
        } else {
            $cuantos = 0;
        }
        return $cuantos;
    }
    
    public function cuentarrayX($arreglo, $dato) {
