    public function BorrarCarpeta($dir){
        $Res = false;
        if ( file_exists($dir) )
        {
            $dh = opendir($dir);
            while ($file=readdir($dh)) 
            {
                if ($file!="." && $file!="..") 
                {
                    $fullpath = $dir."/".$file;
                    if (!is_dir($fullpath)) 
                    {
                        unlink($fullpath);
                    } 
                    else 
                    {
                        BorrarCarpeta($fullpath);
                    }
                }
            }
            closedir($dh);
            if (rmdir($dir)) 
            {
                $Res = true;
            } 
        }
        return $Res;
    }
    
    public function redondeaTiempo($fechahora, $minutos){
        $fecha = date("Y-m-d",strtotime($fechahora));
        $hora = date("G:i:s",strtotime($fechahora));

        $horaactividad=date("G",strtotime($hora));
        $minutoactividad=date("i",strtotime($hora));

        if($minutoactividad % $minutos == 0){
            $ntime = str_pad($minutoactividad, 2, "0", STR_PAD_LEFT);            
            $horaactividad = str_pad($horaactividad, 2, "0", STR_PAD_LEFT);
            $horaredonda = $horaactividad.":".$minutoactividad;
        }else{
           $ntime = round($minutoactividad/$minutos, 0)*$minutos; 
            if($ntime == 60){
                $ntime = '00';
                $horaactividad++;

                if($horaactividad == 24){
                    $horaactividad = '00';
                    $fecha = $this->sumadias($fecha, 1);
                }
            }else{
                $ntime = str_pad($ntime, 2, "0", STR_PAD_LEFT);    
            }           

            $horaactividad = str_pad($horaactividad, 2, "0", STR_PAD_LEFT); 
            $horaredonda = $horaactividad.":".$ntime;           
        }     

        $fechareturn = $fecha." ".$horaredonda;
        return $fechareturn;     
    }    

    public function sumadias($fec_emision, $can_dias) {
        $fecha = explode("-", $fec_emision);
        $dyh = getdate(mktime(0, 0, 0, $fecha[1], $fecha[2], $fecha[0]) + 24 * 60 * 60 * $can_dias);
        $fec_vencimiento = $dyh['year'] . "-" . $this->ceros($dyh['mon']) . "-" . $this->ceros($dyh['mday']);
        return ($fec_vencimiento);
    }

    public function restaFechas($dFecIni, $dFecFin) {
        $date1 = new \DateTime($dFecIni);
        $date2 = new \DateTime($dFecFin);
        $diff = $date1->diff($date2);
        return $diff->days;
    }
    
    public function restahora($fechahora, $minutos){	        
        $nuevafechab = strtotime ( '-'.$minutos.' minute' , strtotime ($fechahora));
        $nuevafecha = date ('Y-m-d G:i:s', $nuevafechab);       
        
        
        return $nuevafecha;
    }      

    public function ceros($numero, $ceros = 2) {
        return sprintf("%0" . $ceros . "s", $numero);
    }

    public function fechaAmericana($fecha) {
        $fechanueva = explode("-", $fecha);
        $year = $fechanueva[0];
        $mes = $fechanueva[1];
        $dia = $fechanueva[2];

        $mesnuevo = $this->mesAbreviado($mes);
        $nueva = $dia . " " . $mesnuevo . ", " . $year;
        return $nueva;
    }

    public function fechaMexicana($fecha) {
