    public function fechaBilling($fecha){
        //YYYY-MM-DD
        $fechita = explode("-", $fecha);
        $year = $fechita[0];
        $mes = $fechita[1];
        $dia = $fechita[2];
        
        $ndia = $this->diaSemanaEN($fecha);
        $mesEN = $this->nombremesEN($mes);
        
        $res = $this->mayusculas($ndia).", ".$this->mayusculas($mesEN)." ".$this->ceros($dia, 2).", ".$year;
        
        return $res;
    }
    
