    public function diaSemanaEN($fecha) {
        $fechanueva = explode("-", $fecha);
        $year = $fechanueva[0];
        $mes = $fechanueva[1];
        $dia = $fechanueva[2];

        // 0->domingo     | 6->sabado
        $dia = date("w", mktime(0, 0, 0, $mes, $dia, $year));
        switch ($dia) {
            case 0:
                $day = "Sunday";
                break;
            case 1:
                $day = "Monday";
                break;
            case 2:
                $day = "Tuesday";
                break;
            case 3:
                $day = "Wednesday";
                break;
            case 4:
                $day = "Thursday";
                break;
            case 5:
                $day = "Friday";
                break;
            case 6:
                $day = "Saturday";
                break;
        }
        return $day;
    }

    public function diaSemanaES($fecha) {
        $fechanueva = explode("-", $fecha);
        $year = $fechanueva[0];
        $mes = $fechanueva[1];
        $dia = $fechanueva[2];

        // 0->domingo     | 6->sabado
        $dia = date("w", mktime(0, 0, 0, $mes, $dia, $year));
        switch ($dia) {
            case 0:
                $day = "Domingo";
                break;
            case 1:
                $day = "Lunes";
                break;
            case 2:
                $day = "Martes";
                break;
            case 3:
                $day = "Miercoles";
                break;
            case 4:
                $day = "Jueves";
                break;
            case 5:
                $day = "Viernes";
                break;
            case 6:
                $day = "Sabado";
                break;
        }
        return $day; 
    }
    
